import Nav from "../components/nav"
import Navdown from "../components/navdown"
import axios, { Axios } from "axios"
import { upload } from "@testing-library/user-event/dist/upload"
import { useState } from "react"

const Create =()=>{
    const[imageSelected,setImageSelected]=useState("")
    // let file= ()=>{
    //     const formData=new FormData()
    //     formData.append("files",imageSelected)
    //     formData.append("upload_preset","zeta-storage");
    //     axios.post("https://api.cloudinary.com/v1_1/dycfohs1u/upload",formData).then((res)=>{
    //         console.log(res)
    //     })
    // }
    // let file=(e)=>{
    //     const reader=new FileReader()
    //     reader.onload=function(onloadevent){
    //         setImageSelected=(onloadevent.target.result)
    //         console.log(imageSelected);
    //     }
    //     reader.readAsDataURL(e.target.files[0])
    // }
    let file=()=>{
        var myWidget = window.cloudinary.createUploadWidget({
            cloudName: 'zeta-web3', 
            uploadPreset: 'my_preset'}, (error, result) => { 
              if (!error && result && result.event === "success") { 
                console.log('Done! Here is the image info: ', result.info); 
              }
            }
          )
        myWidget.open();
    }

    return(
            <div>
                <Nav/>
                <div className="create">
                    <p id="create-p">Create Your Item</p>
                        <div className="create-img">
                                {/* <input type="file" id="create-img-file" onChange={(event)=>{
                                    setImageSelected(event.target.files[0])
                                }}/> */}
                                <input type="file" id="create-img-file" onChange={file}/>
                                <button id="upload_widget" onClick={file}>Upload</button>
                        </div>
                    <div className="create-lebel">
                            <p id="data-name">Name</p>
                            <input type="text" id="data-name-in" name="name"/>

                            <p id="data-name">Details</p>
                            <input type="text" id="data-name-in" name="name"/>

                            <p id="data-name">Price</p>
                            <input type="Number" id="data-name-in" name="name"/>

                            <p id="catagory">Catagory</p>
                                <select id="data-select">
                                    <option value="0">Home & Garden</option>
                                    <option value="1">Other</option>
                                    <option value="3">Electronics</option>
                                </select><br/>
                    </div>
                    <button id="btn-submit"type="Submit">Save</button>
                </div>
                <Navdown/>
            </div>);
}
export default Create;
